import React from "react";

class Regist extends React.Component {
  componentDidMount() {
    var form = document.getElementById("formInsert");
    form.addEventListener("submit", event => {
      const name = document.getElementById("ed_name").value,
        code = document.getElementById("ed_code").value,
        location = document.getElementById("ed_location").value,
        date = document.getElementById("ed_dateBorrow").value,
        pic = document.getElementById("ed_pic").value,
        idBagian = document.getElementById("ed_idBagian").value,
        status = document.getElementById("ed_status").value;

      console.log("email : ", email);
      console.log("password : ", password);
      event.preventDefault();
      fetch("http://localhost:3000/insert", {
        method: "POST",
        headers: {
          "Content-Type": "application/x-www-form-urlencoded"
        },
        body: `name=${name}&code=${code}&location=${location}&date=${date}&pic=${pic}&idBagian=${idBagian}&status=${status}`
      }).then(response => {
        console.log(response);
        if (response.ok) {
          console.log("Item Registered");
        } else {
          console.log("Something Wrong");
        }
      });
    });
  }

  render() {
    return (
      <main>
        <div
          style={{ display: "grid", background: "white" }}
          className="divLogo"
        >
          <img src="static/src/Logo_petro.png" id="logo_petro" />

          <text className="petro">PT.PETROKIMIA GRESIK</text>

          <text className="welcome"> SIGN UP </text>

          <text className="sistem"> Sistem Monitoring Project</text>

          <text className="dept">DEPT. TEKNOLOGI DAN INFORMASI </text>
        </div>

        <form method="post" id="formInsert">
          <text className="name_txt">Name Item</text>

          <input type="string" name="nameItem" id="ed_name" />

          <text className="code_txt">Code Unique</text>

          <input type="text" name="codeItem" id="ed_code" />

          <text className="location_txt">Location</text>
          <input type="text" name="locationItem" id="ed_location" />

          <text className="date_txt">Date</text>
          <input type="date" name="dateItem" id="ed_dateBorrow" />

          <text className="pic_txt">Person in Charge</text>
          <input type="text" name="picItem" id="ed_pic" />

          <text className="idBagian_txt">ID Bagian</text>
          <input type="text" name="idBagianItem" id="ed_idBagian" />

          <text className="pic_txt">Status</text>
          <select className="choice_status" id="ed_status" name="statusItem">
            <option value="Exist">Ready Stock</option>
            <option value="Borrowed">Dipinjam</option>
          </select>

          <button className="regist">Insert Item</button>
        </form>
        <style jsx global>{`
          .petro {
            margin: 0 auto;
            font-size: 20px;
            margin-top: 0%;
            font-family: "Open Sans", sans-serif;
          }

          .dept {
            font-size: 20px;
            margin: 0 auto;
            margin-bottom: 10%;
          }

          .welcome {
            font-size: 40px;
            margin: 0 auto;
            margin-top: 10%;
          }

          .sistem {
            font-size: 20px;
            margin: 0 auto;
            margin-top: 10%;
          }

          body {
            background: #3ec400;
            font-family: "Open Sans", sans-serif;
            margin: 0;
          }

          div {
            margin: 0 auto;
            display: block;
          }

          main {
            display: grid;
            grid-template-columns: 1fr 1fr;
            font-family: "Open Sans", sans-serif;
          }

          .ahref {
            font-size: 13px;
            text-decoration: none;
          }

          .divLogo {
            width: 100%;
            margin: 0 auto;
            height: 646px;
          }

          .regist {
            width: 37%;
            margin: 0 auto;
            margin-top: 4%;
            display: grid;
            font-size: 30px;
            padding: 5px 5px;
            border-radius: 10px;
          }

          .loginBack {
            width: 40%;
            margin: 0 auto;
            margin-top: 3%;
            display: grid;
            font-size: 100%;
            padding: 2px 2px;
            border-radius: 10px;
          }

          #logo_petro {
            background: #ffffff;
            width: 50%;
            margin: 0 auto;
            margin-top: 15%;
          }

          .email_txt {
            margin-top: 2%;
            font-size: 20px;
            display: block;
            color: white;
            margin-left: 24%;
          }

          .gender_txt {
            margin-top: 2%;
            font-size: 20px;
            display: block;
            color: white;
            margin-left: 24%;
          }

          .date_txt {
            margin-top: 2%;
            font-size: 20px;
            display: block;
            color: white;
            margin-left: 24%;
          }

          .gender {
            width: 53%;
            margin: 0 auto;
            margin-top: 0.5%;
            display: block;
            border-radius: 5px;
            font-size: 20px;
            border: none;
            padding: 10px 10px;
            background: orange;
            color: black;
          }

          #ed_bornDate {
            width: 50%;
            margin: 0 auto;
            margin-top: 0.5%;
            display: block;
            border-radius: 5px;
            font-size: 20px;
            border: none;
            padding: 10px 10px;
            background: orange;
            color: black;
          }

          .name_txt {
            margin-top: 7%;
            font-size: 20px;
            display: block;
            color: white;
            margin-left: 24%;
          }

          #ed_name {
            width: 50%;
            margin: 0 auto;
            margin-top: 0.5%;
            display: block;
            border-radius: 5px;
            font-size: 20px;
            border: none;
            padding: 10px 10px;
            background: orange;
            color: black;
          }

          #ed_email {
            width: 50%;
            margin: 0 auto;
            margin-top: 0.5%;
            display: block;
            border-radius: 5px;
            font-size: 20px;
            border: none;
            padding: 10px 10px;
            background: orange;
            color: black;
          }

          .password_txt {
            margin-top: 2%;
            font-size: 20px;
            display: block;
            margin-left: 24%;
            color: white;
          }
          #ed_password {
            width: 50%;
            margin: 0 auto;
            margin-top: 1%;
            display: block;
            background: orange;
            border-radius: 5px;
            font-size: 20px;
            border: none;
            padding: 10px 10px;
            color: black;
          }
        `}</style>
      </main>
    );
  }
}

export default Regist;
