import React from "react";

class Clock extends React.Component {
  /*componentDidMount() {
    var form = document.getElementById("formLogin");
    form.addEventListener("submit", event => {
      const email = document.getElementById("ed_email").value,
        password = document.getElementById("ed_password").value;

      console.log("email : ", email);
      console.log("password : ", password);
      event.preventDefault();
      fetch("http://localhost:3000/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/x-www-form-urlencoded"
        },
        body: `email=${email}&password=${password}`
      }).then(response => {
          console.log("Password is",response.ok)
      });
    });
  }*/

  render() {
    return (
      <main>
        <div
          style={{ display: "grid", background: "white" }}
          className="divLogo"
        >
          <img src="static/src/Logo_petro.png" id="logo_petro" />
        </div>

        <div
          style={{ display: "grid", background: "white" }}
          className="divLogo"
        >
          <h3>
            <a href="../Login">Login</a>
          </h3>
          <h3>
            <a href="../DaftarBarang">Daftar Barang</a>
          </h3>
          <h3>
            <a href="../Panduan">Panduan</a>
          </h3>
        </div>

        <div>
          <text>SISTEM INVENTARIS BARANG</text>
          <text>PT. PETROKIMIA GRESIK</text>
        </div>

        <style jsx global>{`
          body {
            background: #3ec400;
            font-family: "Open Sans", sans-serif;
            margin: 0;
          }

          div {
            margin: 0 auto;
            display: block;
          }

          main {
            display: grid;
            grid-template-columns: 1fr 1fr;
            font-family: "Open Sans", sans-serif;
          }

          .divLogo {
            width: 100%;
            margin: 0 auto;
            height: 120px;
          }

          #logo_petro {
            background: #ffffff;
            width: 30%;
            margin-top: 3%;
            margin-left: 3%;
          }
        `}</style>
      </main>
    );
  }
}

export default Clock;
