import React from "react";

class Clock extends React.Component {
  /*componentDidMount() {
    var form = document.getElementById("formLogin");
    form.addEventListener("submit", event => {
      const email = document.getElementById("ed_email").value,
        password = document.getElementById("ed_password").value;

      console.log("email : ", email);
      console.log("password : ", password);
      event.preventDefault();
      fetch("http://localhost:3000/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/x-www-form-urlencoded"
        },
        body: `email=${email}&password=${password}`
      }).then(response => {
          console.log("Password is",response.ok)
      });
    });
  }*/

  render() {
    return (
      <main>
        <div
          style={{ display: "grid", background: "white" }}
          className="divLogo">
          <img src="static/src/Logo_petro.png" id="logo_petro" />
        </div>

        <div
          style={{ display: "grid", background: "white" }}
          className="divLogo">
          <h3>Login</h3>
        </div>


        <style jsx global>{`
          .petro {
            margin: 0 auto;
            font-size: 20px;
            margin-top: 0%;
            font-family: "Open Sans", sans-serif;
          }

          .dept {
            font-size: 20px;
            margin: 0 auto;
            margin-bottom: 10%;
          }

          .welcome {
            font-size: 40px;
            margin: 0 auto;
            margin-top: 10%;
          }

          .sistem {
            font-size: 20px;
            margin: 0 auto;
            margin-top: 10%;
          }

          body {
            background: #3ec400;
            font-family: "Open Sans", sans-serif;
            margin: 0;
          }

          div {
            margin: 0 auto;
            display: block;
          }

          main {
            display: grid;
            grid-template-columns: 1fr 1fr;
            font-family: "Open Sans", sans-serif;
          }

          .divLogo {
            width: 100%;
            margin: 0 auto;
            height: 120px;
          }


          #logo_petro {
            background: #ffffff;
            width: 30%;
            margin-top: 3%;
            margin-left: 3%;
          }

          .sign_in {
            display: block;
            margin-left: 17%;
            margin-top: 7%;
            font-family: "Open Sans", sans-serif;
            color: white;
          }

          .email_txt {
            margin-top: 12%;
            font-size: 20px;
            display: block;
            color: white;
            margin-left: 24%;
          }

          #ed_email {
            width: 50%;
            margin: 0 auto;
            margin-top: 0.5%;
            display: block;
            border-radius: 5px;
            font-size: 20px;
            border: none;
            padding: 10px 10px;
            background: orange;
            color: black;
          }

          .password_txt {
            margin-top: 3%;
            font-size: 20px;
            display: block;
            margin-left: 24%;
            color: white;
          }
          #ed_password {
            width: 50%;
            margin: 0 auto;
            margin-top: 1%;
            display: block;
            background: orange;
            border-radius: 5px;
            font-size: 20px;
            border: none;
            padding: 10px 10px;
            color: black;
          }
        `}</style>
      </main>
    );
  }
}

export default Clock;
