export default() =>
<main >
    <div style={{display: "grid", background: "white"}} className="divLogo">
        <img src="static/src/Logo_petro.png" id="logo_petro"/>

        <text className="petro">PT.PETROKIMIA GRESIK</text>

        <text className="welcome"> WELCOME </text>

        <text className="sistem"> Sistem Monitoring Project</text>

        <text className="dept">DEPT. TEKNOLOGI DAN INFORMASI </text>
    </div>

    <form method="post" id="formLogin">

    <input
      type = "email"
      name = "email"
      id = "ed_email"
      placeholder = "Email"/>

    <input
      type = "password"
      name = "password"
      id = "ed_password"
      placeholder = "Password"/>

      <button className="login">Login</button>
      <a href="/Registration"><text className="regist">Don't have an account? Register here</text></a>
    </form>
<style jsx global>{`

    .petro{
        margin: 0 auto;
        font-size: 20px;
        margin-top: -8%;
    }

    .dept{
        font-size: 20px;
        margin: 0 auto;
    }

    .welcome{
        font-size: 40px;
        margin: 0 auto;
    }

    .sistem{
        font-size: 20px;
        margin: 0 auto;
    }

    body {
        background: #3EC400;
        font-family: 'Open Sans', sans-serif;
        margin: 0;
    }

    div {
        margin: 0 auto;
        display: block;
    }

    main{
        display: grid;  
        grid-template-columns: 1fr 1fr;
    }

    .ahref{
        font-size: 13px;
        text-decoration: none;
    }

    .divLogo{
        width: 100%;
        margin: 0 auto;
        height: 646px;
    }

    .regist{
        width: 25%;
        position: relative;
        right: 0;
        margin-top: 1%;
        margin: 0 auto;
        display: grid;
        font-size: 15px;
        padding: 5px 5px;
    }

    .login{
        width: 15%;
        position: relative;
        right: 0;
        margin-top: 1%;
        margin: 0 auto;
        display: grid;
        font-size: 30px;
        padding: 5px 5px;
    }

    .divPic{
        background: #FFFFFFF;
        width: 100%;
    }

    #logo_petro{
        background: #FFFFFF;
        width: 50%;
        margin: 0 auto;
        margin-top: 15%;
    }

    #ed_email{
        width: 24%;
        position: relative;
        right: 0;
        margin-top: 0;
        margin: 0 auto;
        display: block;
        border-radius: 10px;
        font-size: 20px;
        border: none;
        border-bottom: 2px inset #000000;
        padding: 10px 10px;
    }

    #ed_password{
        width: 24%;
        position: relative;
        right: 0;
        margin-top: 1%;
        margin: 0 auto;
        display: block;
        border-radius: 10px;
        font-size: 20px;
        border: none;
        border-bottom: 2px inset #000000;
        padding: 10px 10px;
    }
`}</style>
</main>
