import React from "react";
import { response } from "express";

class ListBarang extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      items: []
    }
  }

  componentDidMount() {
    /*fetch("http://localhost:3000/api/data/item")
    .then(response => response.json())
    .then(data => this.setState({items : data}))*/
    var form = document.getElementById("formLogin");
    form.addEventListener("submit", event => {
      const email = document.getElementById("ed_email").value,
        password = document.getElementById("ed_password").value;

      console.log("email : ", email);
      console.log("password : ", password);
      event.preventDefault();
      fetch("http://localhost:3000/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/x-www-form-urlencoded"
        },
        body: `email=${email}&password=${password}`
      }).then(response => {
          console.log("Password is",response.ok)
      });
    });
  }

  render() {
    return (
      <React.Fragment>
        <nav style={{ display: "flex", width: "100%" }} className="nav">
          <div>
            <a href="../Dashboard">
              <img src="static/src/Logo_petro.png" className="logo" />
            </a>
          </div>
          <div
            style={{ justifySelf: "flex-end", width: "50%" }}
            className="text"
          >
            <a className="login">Daftar Barang</a>
            <a href="../InsertBarang" className="daftarBarang">
              Input Barang
            </a>
            <a href="../Panduan" className="panduan">
              Logout
            </a>
          </div>
        </nav>
        <div className="greenStrip"></div>
        <div>
          <ul>
            { items.map((item, index) =>
              <li key={index}> {item.name} </li>
            )}
          </ul>
        </div>
        <style jsx global>{`
          body {
            background: #ffffff;
            font-family: "Open Sans", sans-serif;
            margin: 0 auto;
          }

          .greenStrip {
            display: block;
            margin: 0 auto;
            width: 90%;
            background: #3ec400;
            height: 2px;
          }

          div {
            margin: 0 auto;
            display: block;
          }

          main {
            display: grid;
            grid-template-columns: 1fr 1fr;
            font-family: "Open Sans", sans-serif;
          }

          .logo {
            width: 50%;
            margin: 0;
            margin-left: 25px;
            margin-top: 3%;
          }

          .nav {
            background: white;
            margin: 0;
          }

          .login {
            margin-left: 15%;
            margin-right: 5%;
            font-size: 25px;
            margin-top: 0%;
            text-decoration: none;
            color: black;
          }

          .daftarBarang {
            font-size: 25px;
            margin-right: 5%;
            text-decoration: none;
            color: black;
          }

          .text {
            background: white;
            margin: 0;
            margin-top: 4%;
          }

          .panduan {
            font-size: 25px;
            margin-right: 5%;
            text-decoration: none;
            color: black;
          }
        `}</style>
      </React.Fragment>
    );
  }
}

export default ListBarang;
