import React from "react"
import $ from "jquery"

function auth() {
    var email = $("#ed_email").val()
    var password = $("#ed_password").val()
    const url = new URL(window.location.href)
          param = new URLSearchParams(url.searchParams)
    event.preventDefault()
    const form = document
    // $.ajax({
    //     url: "http://localhost:3000/login",
    //     type: "post",
    //     dataType: "json",
    //     contentType: "application/x-www-form-urlencoded",
    //     data: {
    //         "email" : email,
    //         "password" : password
    //     },
    //     body: new URLSearchParams([...(new FormData(form))])
    // })

    fetch("http://localhost:3000/registration", {
        method: 'POST',
        mode: "no-cors",
        headers: {
            'Content-Type' : 'application/x-www-form-urlencoded'
        },
        body: new URLSearchParams([...URLSearchParams(new FormData(form))])
    }).then( response => console.log(response))
}

export default() =>
<main >
    <div style={{display: "grid", background: "white"}} className="divLogo">
        <img src="static/src/Logo_petro.png" id="logo_petro"/>

        <text className="petro">PT.PETROKIMIA GRESIK</text>

        <text className="welcome"> WELCOME </text>

        <text className="sistem"> Sistem Monitoring Project</text>

        <text className="dept">DEPT. TEKNOLOGI DAN INFORMASI </text>
    </div>

    <form method="post" id="form">

    <input
      type = "email"
      name = "email"
      id = "ed_email"
      placeholder = "Email"/>

    <input
      type = "password"
      name = "password"
      id = "ed_password"
      placeholder = "Password"/>

      <button className="login">Login</button>
      <a href="/Registration"><text className="regist">Don't have an account? Register here</text></a>
    </form>
<style jsx global>{`

    .petro{
        margin: 0 auto;
        font-size: 20px;
        margin-top: -8%;
    }

    .dept{
        font-size: 20px;
        margin: 0 auto;
    }

    .welcome{
        font-size: 40px;
        margin: 0 auto;
    }

    .sistem{
        font-size: 20px;
        margin: 0 auto;
    }

    body {
        background: #3EC400;
        font-family: 'Open Sans', sans-serif;
        margin: 0;
    }

    div {
        margin: 0 auto;
        display: block;
    }

    main{
        display: grid;  
        grid-template-columns: 1fr 1fr;
    }

    .ahref{
        font-size: 13px;
        text-decoration: none;
    }

    .divLogo{
        width: 100%;
        margin: 0 auto;
        height: 646px;
    }

    .regist{
        width: 25%;
        position: relative;
        right: 0;
        margin-top: 1%;
        margin: 0 auto;
        display: grid;
        font-size: 15px;
        padding: 5px 5px;
    }

    .login{
        width: 15%;
