    .divPic{
        background: #FFFFFFF;
        width: 100%;
    }

    #logo_petro{
        background: #FFFFFF;
        width: 50%;
        margin: 0 auto;
        margin-top: 15%;
    }

    #ed_email{
        width: 24%;
        position: relative;
        right: 0;
        margin-top: 0;
        margin: 0 auto;
        display: block;
        border-radius: 10px;
        font-size: 20px;
        border: none;
        border-bottom: 2px inset #000000;
        padding: 10px 10px;
    }

    #ed_password{
        width: 24%;
        position: relative;
        right: 0;
        margin-top: 1%;
        margin: 0 auto;
        display: block;
        border-radius: 10px;
        font-size: 20px;
        border: none;
        border-bottom: 2px inset #000000;
        padding: 10px 10px;
    }
`}</style>
