    //     data: {
    //         "email" : email,
    //         "password" : password
    //     },
    //     body: new URLSearchParams([...(new FormData(form))])
    // })

    fetch("http://localhost:3000/registration")
        .then( response => response.json())
        .then(
            // handle the result
            (result) => {
                this.setState({
                    isLoaded : true,
                    posts : result
                });
            },
            // Handle error 
            (error) => {
                this.setState({
                    isLoaded: true,
                    error
                });
                console.log(error);
            },
        )
}

