import React from "react"

class Regist extends React.Component {

    componentDidMount() {
        var form = document.getElementById('formRegist')
        form.addEventListener('submit', (event) => {
            const email = document.getElementById("ed_email").value,
                  password = document.getElementById("ed_password").value,
                  name = document.getElementById("ed_name").value,
                  date = document.getElementById("ed_bornDate").value,
                  gender = document.getElementById("genders").value


                  console.log("email : ", email)
                  console.log("password : ", password)
                event.preventDefault()
                fetch("http://localhost:3000/registrations", {
                    method: 'POST',
                    headers: {
                        'Content-Type' : 'application/x-www-form-urlencoded'
                    },
                    body: `name=${name}&email=${email}&password=${password}&bornDate=${date}&gender=${gender}`
                }).then( response => {
                    console.log(response)
                    if (response.ok){
                        console.log("User Registered")
                    }else{

                    }
                })
            })
    }

    render() {
        return (
<main>
<div style={{display: "grid", background: "white"}} className="divLogo">
        <img src="static/src/Logo_petro.png" id="logo_petro"/>

        <text className="petro">PT.PETROKIMIA GRESIK</text>

        <text className="welcome"> WELCOME </text>

        <text className="sistem"> Sistem Monitoring Project</text>

        <text className="dept">DEPT. TEKNOLOGI DAN INFORMASI </text>
</div>

    <form method = "post" id = "formRegist">
    <input
      type = "name"
      name = "name"
      id = "ed_name"
      placeholder = "Name"/>

    <input
      type = "email"
      name = "email"
      id = "ed_email"
      placeholder = "Email"/>

    <input
      type = "password"
      name = "password"
      id = "ed_password"
      placeholder = "Password"/>

    <input
      type = "date"
      name = "bornDate"
      id = "ed_bornDate"
      placeholder = "Birth Date"/>

    <select className="gender" id="genders">
        <option value="Male">Male</option>
        <option value="Female">Female</option>
    </select>

      <button className="register">Register<a href = '/Login'></a></button>

      <div className="back"><a href='/Login' className="ahref">Back to Login</a></div>
    </form>


<style jsx global>{`
    body {
        margin: 0;
        background: #3EC400;
        font-family: 'Open Sans', sans-serif;
    }

    .divLogo{
        width: 100%;
        margin: 0 auto;
        height: 646px;
    }

    div {
        margin: 0 auto;
        display: block;
    }

    #logo_petro{
        background: #FFFFFF;
        margin-left: 5%;
        width: 10%;
    }

    .ahref{
        text-decoration: none;
    }

    .register{
        width: 15%;
        position: relative;
        right: 0;
        margin-top: 1%;
        margin-left: 42.5%;
        display: block;
        font-size: 30px;
        padding: 5px 5px;
    }

    #ed_email{
        width: 30%;
        position: relative;
        right: 0;
        margin-top: 1%;
        margin-left: 35%;
        display: block;
        font-size: 20px;
        border: none;
        border-radius: 10px;
        border-bottom: 2px inset #000000;
        padding: 10px 10px;
    }

    #ed_password{
        width: 30%;
        position: relative;
        right: 0;
        margin-top: 1%;
        margin-left: 35%;
        display: block;
        font-size: 20px;
        border-radius: 10px;
        border: none;
        border-bottom: 2px inset #000000;
        padding: 10px 10px;
    }

    #ed_name{
        width: 30%;
        position: relative;
        right: 0;
        margin-top: 10%;
        margin-left: 35%;
        display: block;
        font-size: 20px;
        border: none;
        border-radius: 10px;
        border-bottom: 2px inset #000000;
        padding: 10px 10px;
    }

    #ed_bornDate{
        width: 30%;
        position: relative;
        right: 0;
        margin-top: 1%;
        margin-left: 35%;
        display: block;
        font-size: 20px;
        border: none;
        border-radius: 10px;
        border-bottom: 2px inset #000000;
        padding: 10px 10px;
    }

    .gender{
        width: 31.5%;
        position: relative;
        right: 0;
        margin-top: 1%;
        margin-left: 35%;
        display: block;
        font-size: 20px;
        border: none;
        border-radius: 10px;
        padding: 10px 10px;
    }

    .back{
        width: 25%;
        position: relative;
        right: 0;
        margin-top: 0;
        margin-left: 46.4%;
        font-size: 15px;
        padding: 5px 5px;
    }
`}</style>
</main>

        )
    }
}

export default Regist;
